mapper.select:
SELECT
IFNULL(SUM(AL.point), 0) AS `point`
FROM articleLike AS AL
WHERE
AL.articleId = #{id}
AND AL.memberId = #{memberId}

mapper.update:
UPDATE article
updateDate = NOW(), title = #{title}, body = #{body},
WHERE id = #{id}

UPDATE article
SET hit = hit + 1
WHERE id = #{id}

mapper.delete:
DELETE FROM articleLike
WHERE articleId = #{id}
AND
memberId = #{memberId}
